package basics.task4;
/*Q8. Write a program To print "Welcome to Guvi" 10 times.*/

public class Solution8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{
			System.out.println("Welcome to Guvi");
		}

	}

}
